import React from 'react'
import { Col, Row } from 'react-bootstrap'

const Category = ({filterbycategory , allCategory}) => {
  const onfilter=(cat)=>{
    filterbycategory(cat)
  }
  return (
    <Row>
      <Col sm='12' className='d-flex justify-content-center mb-3'>

        {
          allCategory.length>=1?(allCategory.ma):<h3>null</h3>
        }
        <div>
            <button onClick={()=>onfilter('الكل')} style={{border:'solid 1px #B45B35'}} className='btn mx-2'>الكل</button>
            <button onClick={()=>onfilter('غدا')} style={{border:'solid 1px #B45B35'}} className='btn mx-2'>الغداء</button>
            <button onClick={()=>onfilter('فطار')} style={{border:'solid 1px #B45B35'}} className='btn mx-2'>الفطار</button>
            <button onClick={()=>onfilter('عشاء')} style={{border:'solid 1px #B45B35'}} className='btn mx-2'>العشاء</button>
        </div>
      </Col>
    </Row>
  )
}

export default Category
